                        script: "${curlScript}",
                        returnStdout: true
                    ).trim()
                    .tokenize("\n")

                    if (responseStatusCode != 200 && responseStatusCode != 202 && responseStatusCode != 504) {
                        error("Returned status code = ${responseStatusCode} when calling ${FDC_URL}. Response body: ${responseBody}")
                    } else {
                        echo 'HTTP Response body: ' + responseBody
                    }

execution.script:
pipeline {
    environment {
        FDC_URL = 'https://api.frontdoorcorp.com/fdc/process/events?type=Groundcloud'
        }

    agent any

    stages {
        stage("first") {
            steps {
                script {
                    tz = TimeZone.getTimeZone("America/New_York")
                    def outputDateFormat = "yyyy-MM-dd"
                    def now = new Date() + 1
                    def convertedDate = now.format(outputDateFormat, tz)
                    def curlScript = "curl -s -w '\\n%{response_code}'  GET --header 'Authorization: ApiKey 06bd0656-0d96-11ed-9b93-0242c6062200' ${FDC_URL}"
                    echo curlScript

                    def (String responseBody, int responseStatusCode) = sh(
                        script: "${curlScript}",
                        returnStdout: true
                    ).trim()
                    .tokenize("\n")

                    if (responseStatusCode != 200 && responseStatusCode != 202 && responseStatusCode != 504) {
                        error("Returned status code = ${responseStatusCode} when calling ${FDC_URL}. Response body: ${responseBody}")
                    } else {
                        echo 'HTTP Response body: ' + responseBody
                    }
                }

            }
        }
    }

}
