definition.script:
pipeline {
    environment {
        FDC_URL = 'https://api.frontdoorcorp.com/api/route/optimise/advance'
    }

    agent any

    stages {
        stage("first") {
            steps {
                script {
                    def curlScript = "curl -s -w '\\n%{response_code}' --location --request POST --header 'Content-Type: application/json' --data '{\"parcelCenterIds\":[2,3,4,5,6],\"days\":3}' ${FDC_URL}"

                    def (String responseBody, int responseStatusCode) = sh(
                        script: "${curlScript}",
                        returnStdout: true
                    ).trim()
                    .tokenize("\n")

                    if (responseStatusCode != 200) {
                        error("Returned status code = ${responseStatusCode} when calling ${FDC_URL}. Response body: ${responseBody}")
                    } else {
                        echo 'HTTP Response body: ' + responseBody
                    }
                }
            }
        }
    }
}
