definition.script:
pipeline {
    environment {
        FDC_URL = 'https://api-stage.frontdoorcorp.net/edi/milestone/generate-file'
    }

    agent any

    stages {
        stage("first") {
            steps {
                script {
                    def curlScript = "curl -s -w '####%{response_code}' --location --request POST ${FDC_URL} --header \"Authorization: Client 120\""
                    echo curlScript

                    def (String responseBody, int responseStatusCode) = sh(
                        script: "${curlScript}",
                        returnStdout: true
                    ).trim()
                    .tokenize("####")

                    if (responseStatusCode != 200) {
                        error("Returned status code = ${responseStatusCode} when calling ${FDC_URL}. Response body: ${responseBody}")
                    } else {
                        echo 'HTTP Response body: ' + responseBody + ' and status code: ' + responseStatusCode
                    }
                }
            }
        }
    }
}
